import React, { useState, useRef, useEffect } from 'react';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import { Box, IconButton, Paper, TextField, CircularProgress, Fade, Chip, Typography, Tooltip } from '@mui/material';
import SendIcon from '@mui/icons-material/Send';
import ChatIcon from '@mui/icons-material/Chat';
import CloseIcon from '@mui/icons-material/Close';
import HistoryIcon from '@mui/icons-material/History';
import SearchIcon from '@mui/icons-material/Search';
import styled, { keyframes } from 'styled-components';

// Improved typography theme
const theme = createTheme({
  palette: {
    primary: { main: '#00937c' },
    background: { default: '#f8f9fa' },
    secondary: { main: '#f3f4f6' },
    text: { primary: '#1a1a1a', secondary: '#4a4a4a' },
  },
  typography: {
    fontFamily: '"Inter", "SF Pro Display", "Helvetica Neue", Arial, sans-serif',
    h3: {
      fontWeight: 700,
      fontSize: '1.1rem',
      letterSpacing: '-0.02em',
      color: '#00937c',
      fontFamily: 'inherit',
    },
    body1: {
      fontSize: '0.98rem',
      lineHeight: 1.65,
      letterSpacing: '-0.01em',
      color: '#1a1a1a',
    },
  },
});

// Animations
const fadeInUp = keyframes`
  from { opacity: 0; transform: translateY(20px) scale(0.98); }
  to { opacity: 1; transform: translateY(0) scale(1); }
`;

const pulse = keyframes`
  0% { transform: scale(1); }
  50% { transform: scale(1.03); }
  100% { transform: scale(1); }
`;

const FloatingButton = styled(IconButton)`
  position: fixed !important;
  bottom: 32px !important;
  right: 32px !important;
  width: 64px !important;
  height: 64px !important;
  background: linear-gradient(135deg, #00937c 0%, #00b894 100%) !important;
  color: #fff !important;
  box-shadow: 0 4px 24px rgba(0,147,124,0.25) !important;
  z-index: 9999 !important;
  border-radius: 50% !important;
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1) !important;
  &:hover {
    box-shadow: 0 8px 32px rgba(0,147,124,0.35) !important;
    transform: scale(1.05) !important;
    background: linear-gradient(135deg, #00b894 0%, #00937c 100%) !important;
    animation: ${pulse} 1s ease-in-out;
  }
`;

const ChatPopup = styled(Paper)`
  position: fixed;
  bottom: 112px;
  right: 32px;
  width: 380px;
  max-width: 95vw;
  height: 600px;
  display: flex;
  flex-direction: column;
  border-radius: 24px;
  box-shadow: 0 12px 48px rgba(0,0,0,0.15);
  background: #fff;
  z-index: 9998;
  overflow: hidden;
  animation: ${fadeInUp} 0.4s cubic-bezier(.23,1.01,.32,1) both;
  font-family: inherit;
  @media (max-width: 600px) {
    right: 0;
    left: 0;
    bottom: 0;
    width: 100vw;
    height: 100vh;
    border-radius: 0;
  }
`;

const PopupHeader = styled(Box)`
  display: flex;
  align-items: center;
