import React, { useState, useRef, useEffect } from 'react';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import { Box, IconButton, Paper, TextField, CircularProgress, Fade, Chip } from '@mui/material';
import SendIcon from '@mui/icons-material/Send';
import ChatIcon from '@mui/icons-material/Chat';
import CloseIcon from '@mui/icons-material/Close';
import styled from 'styled-components';

const theme = createTheme({
  palette: {
    primary: { main: '#222' },
    background: { default: '#f8f9fa' },
  },
  typography: {
    fontFamily: '"Inter", "SF Pro Display", "Helvetica Neue", Arial, sans-serif',
  },
});

const FloatingButton = styled(IconButton)`
  position: fixed !important;
  bottom: 32px !important;
  right: 32px !important;
  width: 64px !important;
  height: 64px !important;
  background: linear-gradient(135deg, #222 60%, #4A4A4A 100%) !important;
  color: #fff !important;
  box-shadow: 0 4px 24px rgba(0,0,0,0.18) !important;
  z-index: 9999 !important;
  border-radius: 50% !important;
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1) !important;
  &:hover {
    box-shadow: 0 8px 32px rgba(0,0,0,0.22) !important;
    transform: scale(1.07) !important;
    background: linear-gradient(135deg, #333 60%, #222 100%) !important;
  }
`;

const ChatPopup = styled(Paper)`
  position: fixed;
  bottom: 112px;
  right: 32px;
  width: 380px;
  max-width: 95vw;
  height: 600px;
  display: flex;
  flex-direction: column;
  border-radius: 22px;
  box-shadow: 0 12px 48px rgba(0,0,0,0.2);
  background: #fff;
  z-index: 9998;
  overflow: hidden;
  animation: fadeInUp 0.4s cubic-bezier(.23,1.01,.32,1) both;
  font-family: '"Inter", "SF Pro Display", "Helvetica Neue", Arial, sans-serif';
  @keyframes fadeInUp {
    from { opacity: 0; transform: translateY(40px) scale(0.95); }
    to { opacity: 1; transform: translateY(0) scale(1); }
  }
  @media (max-width: 600px) {
    right: 0;
    left: 0;
    bottom: 0;
    width: 100vw;
    height: 100vh;
    border-radius: 0;
  }
`;

const PopupHeader = styled(Box)`
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 20px 20px 12px 20px;
  background: linear-gradient(135deg, #f8f9fa 0%, #e9ecef 100%);
  border-bottom: 1px solid #dee2e6;
`;

const HeaderTitle = styled.h3`
  margin: 0;
  font-size: 1.1rem;
  font-weight: 600;
  color: #222;
  font-family: inherit;
`;

const MessagesArea = styled(Box)`
  flex: 1;
  overflow-y: auto;
  padding: 20px;
  background: #f8f9fa;
  font-family: inherit;
  &::-webkit-scrollbar {
    width: 6px;
  }
  &::-webkit-scrollbar-track {
    background: transparent;
  }
  &::-webkit-scrollbar-thumb {
    background: #dee2e6;
    border-radius: 3px;
  }
  &::-webkit-scrollbar-thumb:hover {
    background: #adb5bd;
  }
`;

const Bubble = styled(Box).withConfig({ shouldForwardProp: (prop) => prop !== 'isUser' })`
  max-width: 85%;
  margin: 12px 0;
  padding: 16px 20px;
  border-radius: 20px;
  font-size: 0.95rem;
  line-height: 1.5;
  font-family: inherit;
  background: ${({ isUser }) => isUser ? 'linear-gradient(135deg, #222 60%, #4A4A4A 100%)' : '#fff'};
  color: ${({ isUser }) => isUser ? '#fff' : '#222'};
  align-self: ${({ isUser }) => isUser ? 'flex-end' : 'flex-start'};
  box-shadow: ${({ isUser }) => isUser ? '0 3px 12px rgba(34,34,34,0.12)' : '0 2px 8px rgba(34,34,34,0.08)'};
  border-bottom-right-radius: ${({ isUser }) => isUser ? '6px' : '20px'};
  border-bottom-left-radius: ${({ isUser }) => isUser ? '20px' : '6px'};
  transition: all 0.3s ease;
  &:hover {
    transform: translateY(-1px);
    box-shadow: ${({ isUser }) => isUser ? '0 4px 16px rgba(34,34,34,0.16)' : '0 3px 12px rgba(34,34,34,0.12)'};
  }
  & span.emoji {
    font-size: 1.2em;
    vertical-align: middle;
  }
`;

const QuickRepliesArea = styled(Box)`
  padding: 12px 20px;
  background: #fff;
  border-top: 1px solid #f0f0f0;
  display: flex;
  flex-wrap: wrap;
  gap: 8px;
`;

const QuickReplyChip = styled(Chip)`
  font-size: 0.85rem !important;
  font-family: inherit !important;
  background: #f8f9fa !important;
  border: 1px solid #dee2e6 !important;
  color: #495057 !important;
  transition: all 0.2s ease !important;
  cursor: pointer !important;
  &:hover {
    background: #e9ecef !important;
    border-color: #adb5bd !important;
    transform: translateY(-1px) !important;
  }
`;

const InputArea = styled(Box)`
  padding: 16px 20px 20px 20px;
  background: #fff;
  border-top: 1px solid #f0f0f0;
  display: flex;
  align-items: center;
  gap: 12px;
`;

const StyledTextField = styled(TextField)`
  flex: 1;
  & .MuiOutlinedInput-root {
    border-radius: 16px;
    background: #f8f9fa;
    font-size: 0.95rem;
    font-family: inherit;
    transition: all 0.3s ease;
    &:hover {
      background: #e9ecef;
    }
    &.Mui-focused {
      background: #fff;
      box-shadow: 0 0 0 3px rgba(34,34,34,0.1);
    }
  }
  & .MuiOutlinedInput-input {
    font-family: inherit;
  }
`;

function formatMessage(text) {
  let html = text.replace(/\*\*(.*?)\*\*/g, '<strong>$1</strong>');
  html = html.replace(/\n{2,}/g, '</p><p>');
  html = '<p>' + html + '</p>';
  html = html.replace(/<p><\/p>/g, '');
  return html;
}

