export default function App() {
  const [open, setOpen] = useState(false);
  const [messages, setMessages] = useState([
    { text: "Hej! Jag är 1753 Skincare's AI-assistent och holistiska hudterapeut. Jag kan hjälpa dig med allt från hudvård till hudhälsa! ✨", isUser: false }
  ]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const messagesEndRef = useRef(null);

  const quickReplies = [
    "Jag har torr hud, vilka produkter rekommenderar ni?",
    "Hur fungerar ert hudvårdskoncept?",
    "Vart kan jag ladda hem er e-bok?"
  ];

  useEffect(() => {
    if (open) messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages, open]);

  const handleSend = async (messageText = input) => {
    if (!messageText.trim() || loading) return;
    setMessages(prev => [...prev, { text: messageText, isUser: true }]);
    setInput('');
    setLoading(true);
    try {
      const response = await fetch('http://localhost:5001/api/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ message: messageText })
      });
      const data = await response.json();
      setMessages(prev => [...prev, { text: data.reply, isUser: false }]);
    } catch {
      setMessages(prev => [...prev, { text: 'Tyvärr kunde jag inte processa din fråga just nu. Vänligen försök igen eller kontakta christopher@1753skincare.com', isUser: false }]);
    } finally {
      setLoading(false);
    }
  };

  const handleQuickReply = (reply) => {
    handleSend(reply);
  };

  return (
    <ThemeProvider theme={theme}>
      {!open && (
        <FloatingButton onClick={() => setOpen(true)} aria-label="Öppna chatt">
          <ChatIcon sx={{ fontSize: 32 }} />
        </FloatingButton>
      )}
      {open && (
        <ChatPopup>
          <PopupHeader>
            <HeaderTitle>1753 Skincare Assistant</HeaderTitle>
            <IconButton onClick={() => setOpen(false)} size="small" aria-label="Stäng chatt">
              <CloseIcon />
            </IconButton>
          </PopupHeader>
          <MessagesArea>
            {messages.map((msg, i) => (
              <Fade in={true} key={i}>
                {msg.isUser ? (
                  <Bubble isUser>{msg.text}</Bubble>
                ) : (
                  <Bubble isUser={false} dangerouslySetInnerHTML={{ __html: `<span style='display:inline-block;vertical-align:top;'><span style='display:inline-flex;align-items:center;justify-content:center;width:32px;height:32px;border-radius:50%;background:linear-gradient(135deg, #e0e7ef 60%, #b3c0d1 100%);margin-right:12px;margin-top:2px;box-shadow:0 2px 8px rgba(34,34,34,0.08);'><svg width="18" height="18" viewBox="0 0 22 22" fill="none" xmlns="http://www.w3.org/2000/svg"><circle cx="11" cy="11" r="11" fill="#c7d6e6"/><ellipse cx="11" cy="13.5" rx="6" ry="4.5" fill="#fff"/><ellipse cx="8.5" cy="10" rx="1.2" ry="1.5" fill="#222"/><ellipse cx="13.5" cy="10" rx="1.2" ry="1.5" fill="#222"/><path d="M8.5 15c1.5 1 3.5 1 5 0" stroke="#222" stroke-width="1.2" stroke-linecap="round"/></svg></span></span><span style="display:inline-block;vertical-align:top;margin-top:4px;">${formatMessage(msg.text)}</span>` }} />
                )}
              </Fade>
            ))}
            <div ref={messagesEndRef} />
          </MessagesArea>
          <QuickRepliesArea>
            {quickReplies.map((reply, i) => (
              <QuickReplyChip
                key={i}
                label={reply}
                onClick={() => handleQuickReply(reply)}
                size="small"
              />
            ))}
          </QuickRepliesArea>
          <InputArea>
            <StyledTextField
              placeholder="Skriv ditt meddelande..."
              value={input}
              onChange={e => setInput(e.target.value)}
              onKeyDown={e => e.key === 'Enter' && handleSend()}
              disabled={loading}
              size="small"
            />
            <IconButton 
              color="primary" 
              onClick={() => handleSend()}
              disabled={loading || !input.trim()}
              sx={{
                backgroundColor: '#222',
                color: 'white',
                width: 44,
                height: 44,
                '&:hover': {
                  backgroundColor: '#333'
                }
              }}
            >
              {loading ? <CircularProgress size={20} color="inherit" /> : <SendIcon />}
            </IconButton>
          </InputArea>
        </ChatPopup>
      )}
    </ThemeProvider>
  );
}
