import React, { useState, useRef, useEffect } from 'react';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import { Box, IconButton, Paper, TextField, CircularProgress, Fade } from '@mui/material';
import SendIcon from '@mui/icons-material/Send';
import ChatIcon from '@mui/icons-material/Chat';
import CloseIcon from '@mui/icons-material/Close';
import styled from 'styled-components';

const theme = createTheme({
  palette: {
    primary: { main: '#222' },
    background: { default: '#f8f9fa' },
  },
  typography: {
    fontFamily: '"Inter", "Helvetica Neue", Arial, sans-serif',
  },
});

const FloatingButton = styled(IconButton)`
  position: fixed;
  bottom: 32px;
  right: 32px;
  width: 64px !important;
  height: 64px !important;
  background: linear-gradient(135deg, #222 60%, #4A4A4A 100%) !important;
  color: #fff !important;
  box-shadow: 0 4px 24px rgba(0,0,0,0.18);
  z-index: 1000;
  border-radius: 50%;
  transition: box-shadow 0.2s, transform 0.2s;
  &:hover {
    box-shadow: 0 8px 32px rgba(0,0,0,0.22);
    transform: scale(1.07);
    background: linear-gradient(135deg, #333 60%, #222 100%) !important;
  }
`;

const ChatPopup = styled(Paper)`
  position: fixed;
  bottom: 112px;
  right: 32px;
  width: 370px;
  max-width: 95vw;
  height: 520px;
  display: flex;
  flex-direction: column;
  border-radius: 22px;
  box-shadow: 0 8px 40px rgba(0,0,0,0.18);
  background: #fff;
  z-index: 1100;
  overflow: hidden;
  animation: fadeInUp 0.35s cubic-bezier(.23,1.01,.32,1) both;
  @keyframes fadeInUp {
    from { opacity: 0; transform: translateY(40px); }
    to { opacity: 1; transform: translateY(0); }
  }
  @media (max-width: 600px) {
    right: 0;
    left: 0;
    bottom: 0;
    width: 100vw;
    height: 100vh;
    border-radius: 0;
  }
`;

const PopupHeader = styled(Box)`
  display: flex;
  align-items: center;
  justify-content: flex-end;
  padding: 12px 16px 8px 16px;
  background: transparent;
`;

const MessagesArea = styled(Box)`
  flex: 1;
  overflow-y: auto;
  padding: 18px 16px 8px 16px;
  background: #f8f9fa;
`;

const Bubble = styled(Box).withConfig({ shouldForwardProp: (prop) => prop !== 'isUser' })`
  max-width: 80%;
  margin: 10px 0;
  padding: 13px 18px;
  border-radius: 18px;
  font-size: 1rem;
  line-height: 1.6;
  background: ${({ isUser }) => isUser ? 'linear-gradient(135deg, #222 60%, #4A4A4A 100%)' : '#fff'};
  color: ${({ isUser }) => isUser ? '#fff' : '#222'};
  align-self: ${({ isUser }) => isUser ? 'flex-end' : 'flex-start'};
  box-shadow: ${({ isUser }) => isUser ? '0 2px 8px rgba(34,34,34,0.08)' : '0 1px 4px rgba(34,34,34,0.06)'};
  border-bottom-right-radius: ${({ isUser }) => isUser ? '6px' : '18px'};
  border-bottom-left-radius: ${({ isUser }) => isUser ? '18px' : '6px'};
  transition: background 0.2s;
  & span.emoji {
    font-size: 1.25em;
    vertical-align: middle;
  }
`;

const InputArea = styled(Box)`
  padding: 14px 16px;
  background: #fff;
  border-top: 1px solid #f0f0f0;
  display: flex;
  align-items: center;
  gap: 10px;
`;

const StyledTextField = styled(TextField)`
  flex: 1;
  & .MuiOutlinedInput-root {
    border-radius: 14px;
    background: #f8f9fa;
    font-size: 1rem;
  }
`;

function formatMessage(text) {
  let html = text.replace(/\*\*(.*?)\*\*/g, '<strong>$1</strong>');
  html = html.replace(/\n{2,}/g, '</p><p>');
  html = '<p>' + html + '</p>';
  html = html.replace(/<p><\/p>/g, '');
  return html;
}

const AIAvatar = () => (
  <span style={{
    display: 'inline-flex',
    alignItems: 'center',
    justifyContent: 'center',
    width: 36,
    height: 36,
    borderRadius: '50%',
    background: 'linear-gradient(135deg, #e0e7ef 60%, #b3c0d1 100%)',
    marginRight: 10,
    boxShadow: '0 2px 8px rgba(34,34,34,0.08)'
  }}>
    <svg width="22" height="22" viewBox="0 0 22 22" fill="none" xmlns="http://www.w3.org/2000/svg">
      <circle cx="11" cy="11" r="11" fill="#c7d6e6"/>
      <ellipse cx="11" cy="13.5" rx="6" ry="4.5" fill="#fff"/>
      <ellipse cx="8.5" cy="10" rx="1.2" ry="1.5" fill="#222"/>
      <ellipse cx="13.5" cy="10" rx="1.2" ry="1.5" fill="#222"/>
      <path d="M8.5 15c1.5 1 3.5 1 5 0" stroke="#222" strokeWidth="1.2" strokeLinecap="round"/>
    </svg>
  </span>
);

export default function App() {
  const [open, setOpen] = useState(false);
  const [messages, setMessages] = useState([
    { text: "Hej! Hur kan jag hjälpa dig med din hudvård idag?", isUser: false }
  ]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    if (open) messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages, open]);

  const handleSend = async () => {
    if (!input.trim() || loading) return;
    setMessages(prev => [...prev, { text: input, isUser: true }]);
    setInput('');
    setLoading(true);
    try {
      const response = await fetch('http://localhost:5001/api/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ message: input })
      });
      const data = await response.json();
      setMessages(prev => [...prev, { text: data.reply, isUser: false }]);
    } catch {
      setMessages(prev => [...prev, { text: 'Tyvärr kunde jag inte processa din fråga just nu.', isUser: false }]);
    } finally {
      setLoading(false);
    }
  };

  return (
    <ThemeProvider theme={theme}>
      {!open && (
        <FloatingButton onClick={() => setOpen(true)} aria-label="Öppna chatt">
          <ChatIcon sx={{ fontSize: 34 }} />
        </FloatingButton>
      )}
      {open && (
        <ChatPopup>
          <PopupHeader>
            <IconButton onClick={() => setOpen(false)} size="large" aria-label="Stäng chatt">
              <CloseIcon />
            </IconButton>
          </PopupHeader>
          <MessagesArea>
            {messages.map((msg, i) => (
              <Fade in={true} key={i}>
                <Bubble isUser={msg.isUser} dangerouslySetInnerHTML={!msg.isUser ? { __html: formatMessage(msg.text) } : undefined}>
                  {!msg.isUser && <AIAvatar />}
                  {msg.isUser ? msg.text : null}
                </Bubble>
              </Fade>
            ))}
            <div ref={messagesEndRef} />
          </MessagesArea>
          <InputArea>
            <StyledTextField
              placeholder="Skriv ditt meddelande..."
              value={input}
              onChange={e => setInput(e.target.value)}
              onKeyDown={e => e.key === 'Enter' && handleSend()}
              disabled={loading}
              size="small"
            />
            <IconButton color="primary" onClick={handleSend} disabled={loading || !input.trim()}>
              {loading ? <CircularProgress size={22} /> : <SendIcon />}
            </IconButton>
          </InputArea>
        </ChatPopup>
      )}
    </ThemeProvider>
  );
}
